class Solution:

    @cache
    def tryToJump(self, cur, jump, wasBack, k):
        # print(cur, jump, wasBack, k)
        if (cur > (k+1)*2):
            return 0
        res = 0
        if (cur == k):
            res +=  1
        if (wasBack == 0):
            res += self.tryToJump(cur-1, jump, 1, k)
        res += self.tryToJump(cur+(1<<jump), jump+1, 0, k)
        return res

    def waysToReachStair(self, k: int) -> int:
        return self.tryToJump(1, 0, 0, k)
