class Solution {
public:
    long long binexp(long long a,long long b,long long m=1000000000000000000){
    a%=m;
    long long res=1;
    while(b){
        if(b&1) res*=a;
        a*=a;
        a%=m;
        res%=m;
        b>>=1;
    }
    return res;
}
    int waysToReachStair(int k) {
        int m=1e9+7;
        vector<long long> fac,invfac;
        int j=0,add=1,ct=1,p;
        long long ans=0,temp=1;
        fac.push_back(1);
        invfac.push_back(1);
        for(int i=1;i<31;i++){
            temp=temp*i;
            temp%=m;
            fac.push_back(temp);
            invfac.push_back(binexp(temp,m-2,m));
        }
        while(1+j-ct<=k){
            //cout<<j<<"\n";
            if(k<=1+j){
                p=1+j-k;
                //cout<<p<<"\n";
                temp=fac[ct]*invfac[p];
                temp%=m;
                temp*=invfac[ct-p];
                temp%=m;
                cout<<temp<<"\n";
                ans+=temp;
            }
            j+=add;
            add<<=1;
            ct++;
        }
        return ans;
    }
};
