from math import factorial

class Solution:
    def waysToReachStair(self, k: int) -> int:
        sum_up = 0
        answer = 0

        if k <= 1:
            answer += 1

        for up in range(40):
            sum_up += (1 << up)
            down = (1 + sum_up - k)

            if down < 0 or down > up + 2:
                continue

            # print(up, down, answer, k)

            answer += (factorial(up + 2) // factorial(up + 2 - down) // factorial(down))
            # print(factorial(up + 1) // factorial(up + 1 - down) // factorial(down))

        return answer
