class Solution:
    def __init__(self):
        self.clones = {}

    def rasengan(self, k, i, j, c):
        if i > k+5 or j > 31:
            return 0

        key = (i, j, c)
        if key in self.clones and self.clones[key] != -1:
            return self.clones[key]

        hits = i == k
        if c:
            hits += self.rasengan(k, i-1, j, 0)
        if i + (1 << j) <= k+1:
            hits += self.rasengan(k, i+(1 << j), j+1, 1)

        self.clones[key] = hits
        return hits

    def waysToReachStair(self, k):
        return self.rasengan(k, 1, 0, 1)
