class Solution {
public:

    long long K;

    int helper(int curr,int jump,int backed,map<vector<int>,long long> &dp,map<vector<int>,long long> &check){

        if(curr>K+1)return 0;

        if(check[{curr,jump,backed}]!=0)return dp[{curr,jump,backed}];

        long long ans=0;

        if(curr==K)ans=1;

        if(backed==0)ans+=helper(curr-1,jump,1,dp,check);
        ans=(ans+helper(curr+pow(2,jump),jump+1,0,dp,check));

        check[{curr,jump,backed}]++;

        return dp[{curr,jump,backed}]=ans;
    }
    int waysToReachStair(int k) {

        K=k;

        if(k==0)return 2;
        map<vector<int>,long long> dp,check;

        return helper(1,0,0,dp,check);

    }
};
