class Solution:
    def waysToReachStair(self, k: int) -> int:
        def nCk(n, k):
            return math.comb(n, k)

        diff = k - 1

        answer = 0
        jumpTotal = 0
        numBackwards = 1

        while jumpTotal - numBackwards <= diff:
            numBackNeeded = jumpTotal - diff

            if numBackNeeded >= 0:
                answer += nCk(numBackwards, numBackNeeded)

            jumpTotal <<= 1
            jumpTotal |= 1
            numBackwards += 1

        return answer
