class Solution {
public:
  map<pair<pair<int, int>, int>, int> mp;

int f(int &k, long long i, long long p, int n) {
    if (i > k + 1) return 0;


    auto key = make_pair(make_pair(i, p), n);
    if (mp.find(key) != mp.end()) return mp[key];

    long long x = 0;
     if (i == k)  x=1;
    if (p == 0) {
        x += f(k, i - 1, 1, n);
    }

    long long y = 0;
    if (i + pow(2, n) <= k + 1) {
        y += f(k, i + pow(2, n), 0, n + 1);
    }

    mp[key] = x + y;
    return mp[key];
}

int waysToReachStair(int k) {
    return f(k, 1, 0, 0);

    }
};
