class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k==0:
            return 2
        if k==1:
            return 4
        if k==2:
            return 4
        k-=1
        res=0
        for n in range(3,34):
            hi=(1<<(n-1))-1
            lo=hi-n
            if lo<=k<=hi: res+=math.comb(n, hi-k)
        return res
