class Solution {
public:
    bool check(int temp, int k) {
        return (k <= (1ll << temp) && k >= (1ll << temp) - temp - 1);
    }
    int waysToReachStair(int k) {
        vector<vector<int> > c(32, vector<int>(32));
        for (int i = 0; i < 32; i++) {
            for (int j = i; j < 32; j++) {
                if (i == 0) c[i][j] = 1;
                else {
                    c[i][j] = c[i-1][j-1] + c[i][j-1];
                }
            }
        }
        long long ans = 0;
        for (int i = 0; i < 31; i++) {
            if (check(i, k)) ans += c[(1<<i) - k][i+1];
        }
        return ans;
    }
};
