class Solution {
public:

    int C(int n, int k) {
        double res = 1;
        for (int i = 1; i <= k; ++i)
            res = res * (n - k + i) / i;
        return (int)(res + 0.01);
    }

    int waysToReachStair(int k) {
        int d=k-1;
        int jump=0;
        int cnt=-1;
        while(cnt<=d){
            cnt+=(1<<jump)-1;
            jump++;
            // cout<<cnt<<" "<<jump<<"\n";
        }
        jump-=2;
        int ans=0;
        cnt=1;
        int sum=0;
        cout<<jump<<" "<<d<<"\n";
        for(int i=0;i<=jump;i++){
            cnt++;
            sum+=(1<<i);
            int s=sum-d;
            cout<<s<<" "<<cnt<<"\n";
            if(s<=cnt && s>=0) ans+=C(cnt,s);
        }
        if(d==-1 || d==0) ans++;
        return ans;
    }
};
