using System;
using System.Collections.Generic;

public class Solution {
    private Dictionary<(int, int, bool), int> dp = new Dictionary<(int, int, bool), int>();

    private int Solve(int k, int i, int jump, bool can) {
        if (i > k + 5) return 0;
        if (jump > 31) return 0;

        var key = (i, jump, can);
        if (dp.ContainsKey(key)) return dp[key];

        int ans = (i == k) ? 1 : 0;
        if (can) ans += Solve(k, i - 1, jump, false);
        if (i + (1 << jump) <= k + 1) ans += Solve(k, i + (1 << jump), jump + 1, true);

        dp[key] = ans;
        return ans;
    }

    public int WaysToReachStair(int k) {
        return Solve(k, 1, 0, true);
    }
}
