class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i:int, jump:int = 0, isPrevUsed:bool = False):            
            res = 0

            if i > k+1:
                return 0
            elif i == k:
                res += 1

            res += dfs(i+(1 << jump), jump+1, False) + (0 if (isPrevUsed or i == 0) else dfs(i-1, jump, True))
            return res

        return dfs(1)
