#define mod 1000000007
class Solution {
public:
    long long fact[101];

    Solution(){
        fact[0] = 1;
        for (int i = 1; i <= 100; i++)
            fact[i] = (fact[i - 1] * i) % mod;
    }

    long long power(long long x,  long long y, long long p=mod){
        long long res = 1;
        x = x % p; 
        while (y > 0){
            if (y & 1)
                res = (res * x) % p;
            y = y >> 1; 
            x = (x * x) % p;
        }
        return res;
    }

    long long inv(long long n, long long p = mod){
        return power(n, p - 2, p);
    }
    long long ncr(long long n, long long r, long long p=mod){
        if (n < r)
            return 0;
        if (r == 0)
            return 1;
        return (fact[n] * inv(fact[r], p) % p* inv(fact[n - r], p) % p)% p;

    }
    int waysToReachStair(int k) {
        int ans =0 , jump = 0;
        long long val = 1;
        while (1){
            int minus = val - k;
            if (minus<=jump + 1){
                if (minus >= 0)
                ans += ncr(jump + 1, minus );
            }else
                break;
            val += (1ll<<jump);
            jump++;
        }
        return ans;
    }
};
