class Solution {
private:
    int ncr(int n, int r){
        long ans=1;
        for(int i=1; i<=r; i++){
            ans*=(n-r+i);
            ans/=i;
        }
        return (int)ans;
    }
public:
    int waysToReachStair(int k) {
        int ans=0;
        for(int i=0; i<30; i++){
            if((1<<i) < k) continue;
            if((1<<i)-(i+1) > k) break;

            ans+=ncr(i+1, (1<<i)-k);
        }

        return ans;
    }
};
