ll gcdd(int a,int b)
{
    if(b==0) return a;
    return gcdd(b,a%b);
}

ll ncr_gcd(int n, int r)
{
    long long p = 1, k = 1;
    r=min(r,n-r);
    if (r != 0)
    {
        while (r)
        {
            p *= n;
            k *= r;

            long long m = gcdd(p,k);

            p /= m;
            k /= m;

            n--;
            r--;
        }
    }
    else p = 1;
    return p;
}

