class Solution:
    def waysToReachStair(self, k: int) -> int:


        @cache
        def rec(stair, jump, type_):
            if stair < 0:
                return 0

            if stair > int(5*1e9):
                return 0

            ans = 0

            if stair == k:
                ans = 1

            if type_ == 0:
                ans += rec(stair-1, jump, 1)

            stair_next = stair + int(pow(2, jump))
            ans += rec(stair_next, jump+1, 0)

            return ans

        ans = rec(1, 0, 0)
        return ans
