#define ll long long int

unordered_map<ll, unordered_map<ll,int>>mp;

class Solution {
public:
    int waysToReachStair(int k) {

        mp.clear();

        return solve(1,0,k);  

    }


    int solve(ll i, ll jmp, int k){

        if(i>=k){

            if(i==k || i-1==k)
                return 1+solve(i+pow(2,jmp),jmp+1,k)+solve(i-1+pow(2,jmp),jmp+1,k);

            return 0;

        }


        if(mp[i].count(jmp))
            return mp[i][jmp];


        return mp[i][jmp]= solve(i+pow(2,jmp),jmp+1,k)+solve(i-1+pow(2,jmp),jmp+1,k);




    }
};
