ll c[33][33];

class Solution {
public:
    int waysToReachStair(int k) {
        CL(c,0);
        REP(i,33){
            c[i][i]=c[i][0]=1;
            FOR(j,1,i)c[i][j]=(c[i-1][j]+c[i-1][j-1]);
        }
        int r =0;
        REP(i,40){
            int tk=k+i-1;
            int nb = __builtin_popcount(tk);
            //cout<<i<<' '<<tk<<' '<<nb<<' '<<(1ll<<nb)-1<<' '<<(tk==(1ll<<nb)-1)<<endl;
            if(tk==0)r++;
            else if(tk>=0 && nb>=0 && tk==(1ll<<nb)-1 && nb>=i-1){
                r+=c[nb+1][i];
            }

        }
        return r;
    }
};
