class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def solve(curr, jump, back):
            if curr > (k+10):
                return 0
            res = 0
            if curr == k:
                res += 1
            if back == False:
                res += solve(curr-1, jump, True)
            res += solve(curr+2**jump, jump+1, False)
            return res

        return solve(1, 0, False)
