from scipy.special import comb

class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        if k in [0, 1]:
            ans += 1
        current_j = 0
        current = 1
        while True:
            current += 2 ** current_j
            current_j += 1
            # print(current, current_j)
            if current < k:
                continue
            if current == k:
                ans += 1
                continue
            if (current - k) > current_j + 1:
                break
            ans += int(comb(current_j + 1, current - k))
        return ans
