class Solution {
public:
    int dp[40][40][2];

    int rec(int z, int o, int c){
        if (c>=2 || z<0 || o<0)return 0;
        if (z==0 && o==0)return 1;
        if (dp[z][o][c] != -1)return dp[z][o][c];
        int ans = 0;
        if (o>=1){
            ans += rec(z,o-1,0);
        }
        if (z>=1){
            ans += rec(z-1,o,c+1);
        }
        return dp[z][o][c] = ans;
    }

    int find_answer(int z, int o, int c){
        if (z==0 && o==0 && c==0)return 1;
        //cout<<z<<" "<<o<<" "<<c<<"\n";
        for (int i=0;i<=z+1;i++){
            for (int j=0;j<=o+1;j++){
                for (int k=0;k<2;k++){
                    dp[i][j][k] = -1;
                }
            }
        }
        int sum = 0;
        if (c==0){
            sum += rec(z-1,o,1);
        }
        else {
            sum += rec(z,o-1,0);
        }
        //cout<<sum<<"\n";
        return sum;
    }

