#define MOD 1000000007
#define INF 1e18
#define endl "\n"
#define pb push_back
#define ppb pop_back
#define ff first
#define ss second
#define sz(x) ((int)(x).size())
#define all(x) (x).begin(), (x).end()

class Solution {
public:
    long long nCr(int n, int r){
        if(n < r)
            return 0;
        long double sum = 1;
        for(int i = 1; i <= r; i++){
            sum = sum * 1LL * (n - r + i) / i;
        }
        return sum;
    }

    int waysToReachStair(int k) {
        long long add = 0, pos = 1, sub = -1, ans = 0;

        long long l = pos + add + sub, r = pos + add;
        if(l <= k and k <= r)
            ans++;

        for(int i = 0; i < 31; i++) {
            add ^= (1 << i);
            sub--;
            l = pos + add + sub, r = pos + add;
            int cnt = r - k;

            if(l <= k and k <= r) {
                ans += nCr(abs(sub), cnt);
            }
        }

        return ans;
    }
};
