class Solution:
    def waysToReachStair(self, k: int) -> int:
        memo = {}
        def util(crntStair,jumps,usedStepDown) :
            if crntStair > k + 1  :
                return 0 
            if (crntStair,jumps,usedStepDown) not in memo :
                val = int(crntStair == k)
                if not usedStepDown and crntStair != 0 :
                    val += util(crntStair-1,jumps,True)
                val += util(crntStair + 2**jumps,jumps+1,False)
                memo[(crntStair,jumps,usedStepDown)] = val
            return memo[(crntStair,jumps,usedStepDown)]
        return util(1,0,False)
