class Solution {
    long long mod = 1e9+7;
    int ans = 0;
private:
    int hlper(long long i, int flag, int jump, int& k, unordered_map<string, long long>& mp) {
        if(i > k+1) return 0;
        if(i == k) return 1 + (flag==0?hlper(i-1, 1, jump, k, mp): 0)+ hlper(i+(1LL<<jump), 0, jump+1, k, mp);

        // cout << i << " " << flag << " " << jump << " " << k << endl;
        string hash = to_string(i) +"_"+ to_string(flag) + "_" + to_string(jump);
        if(mp.find(hash) != mp.end()) return mp[hash];

        long long a = 0;
        if(flag == 0 && i>0) {
            a += hlper(i-1, 1, jump, k, mp);
            a %= mod;
        }
        a += hlper(i+(1LL<<jump), 0, jump+1, k, mp);
        a %= mod;
        return mp[hash] = a;
    }
public:
    int waysToReachStair(int k) {
        unordered_map<string, long long> mp;
        return hlper(1, 0, 0, k, mp);
    }
};
