#include <bits/stdc++.h>
using namespace std;

class Solution {
#define ar2d(t, n, m) = array<array<t, m>, n>;
#define outside(i, j, x, y, u, v) (i < x || i > u || j < y || j > v)
#define all(v) v.begin(), v.end()

public:
	using ll = long long;
	using ull = unsigned long long;
	using pii = pair<int, int>;
	using pll = pair<ll, ll>;

	const int RW4[4] = {0, 1, 0, -1};
	const int CL4[4] = {1, 0, -1, 0};
	const int RW8[8] = {0, 1, 1, 1, 0, -1, -1, -1};
	const int CL8[8] = {1, 1, 0, -1, -1, -1, 0, 1};

	const ll INF = 1e18 + 1;
	const int N = 4e5 + 2107;
	const int NN = 3e3 + 1;

	int n, k;
	unordered_map<int, unordered_map<int, unordered_map<int, int>>> memo;

	int dp(int k, int j, int b) {
		if (k > n + 1) return 0;
		if (k == n) {
			if (j > 1) return 1;
			if (j == 1 && b == 0) return 2;
			int res = 1;
			if (j == 0) {
				if (k > 0 && b == 0)
					res += dp(k - 1, j, 1);
				res += dp(k + 1, j + 1, 0);
			}
			return res;
		}
		if (memo.count(k) && memo[k].count(j) && memo[k][j].count(b)) return memo[k][j][b];

		int res = 0;
		if (k > 0 && b == 0)
			res += dp(k - 1, j, 1);
		if (j < 31)
			res += dp(k + (1 << j), j + 1, 0);

		memo[k][j][b] = res;
		return res;
	}

	int waysToReachStair(int k) {
		n = k;
		return dp(1, 0, 0);
	}
};
