class Solution {
public:
    map<int, map<int, map<long long, int>>> dp;
    int calc(int val ,int i, int k, int prev){
        if(val > k + 10)
            return 0;
        if(dp.count(i) && dp[i].count(prev) && dp[i][prev].count(val))
            return dp[i][prev][val];
        int curr = (val == k);
        if(prev != 0)
            curr += calc(val - 1, i, k, 0);
        curr += calc(val + (1 << i), i + 1, k , 1);
        return   dp[i][prev][val] = curr;
    }
    int waysToReachStair(int k) {
        int ans = calc(1, 0, k, 1);
        return ans;
    }
};
