from math import comb

class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 1 if k <= 1 else 0
        jump = [1]
        while True:
            curr_sum = sum(jump)
            n = len(jump)
            if curr_sum - n - 1 <= k - 1 <= curr_sum:
                d = curr_sum - (k - 1)
                ans += comb(n + 1, d)
            jump.append(jump[-1] * 2)
            if jump[-1] > k * 2:
                break

        return ans
