map<vector<int>, int> dp;

int solve(int i, int j, int f, int k)
{
    if(i-k>1)
        return 0;

    if(dp.find({i,j,f})!=dp.end())
        return dp[{i,j,f}];

    int ans=0;

    if(i==k)
        ans++;

    if(i>0 && f)
        ans+=solve(i-1,j,!f,k);

    ans+=solve(i+(1<<j),j+1,1,k);

    //cout<<i<<" "<<j<<" "<<f<<" "<<ans<<endl;
    return dp[{i,j,f}]=ans;
}

class Solution {
public:
    int waysToReachStair(int k) {
        dp.clear();
        return solve(1,0,1,k);
    }
};
