class Solution:
    def waysToReachStair(self, k: int) -> int:
        def lcf(a, b):
            if b==0:
                return a
            return lcf(b, a%b)
        def solve(a, b):
            values = list(range(a, a-b, -1))
            for i in range(2, b+1):
                while i!=1:
                    for j in range(b):
                        t = lcf(values[j], i)
                        values[j]//=t
                        i//=t
            total = 1
            for value in values:
                total*=value
            return total
        res = 0
        stair = 1
        for i in range(1, 100):
            if stair-i>k:
                break
            t = stair-k
            if t>=0:
                res += solve(i, t)
            # print(stair, i, stair-k)
            stair+=(1<<(i-1))


        return res
