class Solution:
    def waysToReachStair(self, k: int) -> int:
        vis = set()
        @cache
        def dfs(pos, jump, prev_back):
            res = 0
            if pos == k:
                res += 1
            elif pos-1 == k and not prev_back:
                return dfs(pos-1, jump, True)
            elif pos > k:
                return 0
            res += dfs(pos+pow(2,jump), jump+1, False)
            if not prev_back:
                res += dfs(pos-1, jump, True)
            return res
        return dfs(1,0,False)
