class Solution {
public:
    int ncr(int n, int r){
        long double sum = 1;
        for(int i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }
        return (sum);
    }
    int waysToReachStair(int k) {
        // if(k == 0) return 2;
        // if(k == 1) return 4;
        int j = 0, s = 1, p2 = 1, ans = 0;
        if(s-1 <= k && k <= s){
            ans += ncr(1,s-k);
        }
        s += p2;
        p2*=2;
        j++;
        while(s < 1e9){
            int c = j+1;
            if(s-c <= k && k <= s){
                ans += ncr(c,s-k);
            }
            s += p2;
            p2*=2;
            j++;
        }
        return ans;
    }
};
