class Solution {
public:
    long long printNcR(int n, int r)
{
    long long p = 1, k = 1;
    if (n - r < r)
        r = n - r;

    if (r != 0) {
        while (r) {
            p *= n;
            k *= r;
            long long m = __gcd(p, k);
            p /= m;
            k /= m;

            n--;
            r--;
        }
    }

    else
        p = 1;

    return p ;
}
    int waysToReachStair(int k) {
        if(k==0)return 2;
        if(k==1)return 4;
        int ans=0;
        int j=0;
        while((1<<j)<=(k+1)){
            int t = k - (1<<(j+1));
            if(t<=0) {
                t=abs(t);
                if(t<=(j+2))ans+=printNcR(j+2,j+2-t); 
            }
            j++;
        }
        return ans;

    }
};
