def ch(k,j,p,d):
    if(k<0):
        return 0
    if(d[(k,j,p)]!=-1):
        return d[(k,j,p)]
    a=0
    if(k==1):
        a=1
    if(p==0):
        a+=ch(k+1,j,1,d)
    else:
        p=0
    a+=ch(k-2**j,j+1,p,d)
    d[(k,j,p)]=a
    return a
class Solution:
    def waysToReachStair(self, k: int) -> int:
        d=defaultdict(lambda:-1)
        return ch(k,0,0,d)
