class Solution(object):
    def __init__(self):
        self.dp = {}

    def solve(self, k, i, jump, can):
        if i > k + 5 or jump > 31:
            return 0
        if (i, jump, can) in self.dp:
            return self.dp[(i, jump, can)]

        ans = (i == k) + (self.solve(k, i - 1, jump, False) if can else 0) + (
            self.solve(k, i + (1 << jump), jump + 1, True) if i + (1 << jump) <= k + 1 else 0)

        self.dp[(i, jump, can)] = ans
        return ans

    def waysToReachStair(self, k):
        return self.solve(k, 1, 0, True)
