pows = [1]
for _ in range(60):
    pows.append(pows[-1]*2)


class Solution(object):
    def waysToReachStair(self, k):
        """
        :type k: int
        :rtype: int
        """

        dic = {}
        def getnext(curr, jump, status):
            if (curr, jump, status) in dic:  return dic[(curr,jump,status)]
            if curr > k + 1:  return 0
            output = 0
            if curr==k:
                output = 1
            if status==0:
                output += getnext(curr-1, jump, 1)

            output += getnext(curr+pows[jump],jump+1,0)
            dic[(curr, jump, status)] = output
            return output 

        ans = getnext(1,0,0)

        return ans
