class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        @cache
        def dfs(cur, jump, prev):
            if cur < 0:
                return 0
            if cur == k + 1 and prev == 0:
                return 0
            if cur > k + 1:
                return 0

            output = 0
            if cur == k:
                output = 1

            if prev == 1:
                output += dfs(cur - 1, jump, 0) 

            output += dfs(cur + pow(2, jump), jump + 1, 1)
            return output
        return dfs(1, 0, 1)
