class Solution
{   
    int func(int n, vector<int> v)
    {
        if (n < 10)
        {
            int tmp = n * (n + 1);

            tmp = tmp >> 2;

            return (n * (n + 1)) / 2;
        }

        int sz = (int)(log10(n));

        int val = (int)(ceil(pow(10, sz)));

        int tmp = n / val;

        int arrayforreturn = func(n % val, v) + tmp * v[sz] + tmp * (1 + n % val) + ((tmp - 1) * tmp / 2) * val;

        return arrayforreturn;
    }


    long long factor(int n)
    {
        long long cnt = 0;

        for (int i = 1; i <= sqrt(n); i++)
        {
            if (n % i == 0)
            {
                if (n / i == i)
                {
                    cnt++;
                }
                else
                {
                    cnt = cnt + 2;
                }
            }
        }

        return cnt;
    }

    int myFunc(int k, int i, int cnt, bool isTurn, unordered_map<int, vector<vector<int>>>& vec)
    {
        if (i >= k+6)
        {
            return 0;
        }

        if (cnt >= 32)
        {
            return 0;
        }

        if (vec.count(i) && vec[i][cnt][isTurn] != -1)
        {
            return vec[i][cnt][isTurn];
        }

        int toReturn;

        if(i == k)
        {
            toReturn = 1;
        }
        else
        {
            toReturn = 0;
        }

        if (isTurn == 1)
        {   
             int tind = i-1;

             toReturn = toReturn + myFunc(k, tind, cnt, 0, vec);
        }

        int tind = i + (1 << cnt);
        int t = k+1;

        if (tind <= t)
        {   
            int tcnt = cnt+1;

            toReturn += myFunc(k, tind, tcnt, 1, vec);
        }

        vec[i].resize(32, vector<int>(2, -1));

        return vec[i][cnt][isTurn] = toReturn;
    }

public:
    int waysToReachStair(int k)
    {   
        unordered_map<int, vector<vector<int>>> vec;

        int ans;

        ans = myFunc(k, 1, 0, 1, vec);

        return ans;
    }
};
