class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4
        if k == 2:
            return 4

        step = 0
        cur_sum = 1
        while cur_sum + (1 << step) < k:
            cur_sum += (1 << step)
            step += 1
        spare = cur_sum + (1 << step) - k
        #print(step + 2, spare)
        res = math.comb(step + 2, spare)
        if  cur_sum + (1 << step) + (1 << (step) + 1) - k <= step + 3:
            res += math.comb(step + 3, cur_sum + (1 << step) + (1 << (step) + 1) - k)
        return res
