class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def solve(pos, jump, used):
            nonlocal k
            possible = 0
            if pos == k:
                possible = 1
            if pos > k + 1:
                return 0
            print(pos, jump, used)
            goDown = 0
            if not used and pos > 0:
                goDown = solve(pos-1, jump, True)
            goUp = solve(pos + 2**jump, jump + 1, False)
            return possible + goDown + goUp

        return solve(1, 0, False)
