#define all(x) (x).begin(),(x).end()
using ll=long long;
using vi=vector<int>;
const ll MOD=1e9+7;
ll modexp(ll b,ll e){
  ll res=1;
  while(e>0){
    if(e&1){
      res*=b; res%=MOD;
    }
    b*=b; b%=MOD;
    e>>=1;
  }
  return res%MOD;
}
class Solution {
public:
  ll fct(int n){
    if(n==0){
      return 1;
    }
    ll res=1;
    for(int i=1;i<=n;i++){
      res*=i; res%=MOD;
    }
    return res;
  }
  int C(int n,int r){
    // cout<<"  ->"<<n<<" "<<r<<"\n";
    ll res=1;
    res*=fct(n); res%=MOD;
    res*=modexp(fct(n-r),MOD-2); res%=MOD;
    res*=modexp(fct(r),MOD-2); res%=MOD;
    return res;
  }
  int waysToReachStair(int k){
    int ans=0;
    for(int i=0;true;i++){
      // cout<<"  ->i="<<i<<"\n";
      int d=((1<<i)-1)-(k-1);
      // cout<<"  ->d="<<d<<"\n";
      if(d>i+1){
        break;
      }
      if(d>=0){
        ans+=C(i+1,d);
      }
      // cout<<"  ->ans="<<ans<<"\n";
    }
    cout<<C(5,1)<<"\n";
    // cout<<"\n";
    return ans;
  }
};
