class Solution:
    def waysToReachStair(self, k: int) -> int:

        res = [1]
        i = 0
        ans = 0
        while res[-1] - len(res) <= k:
            if res[-1] == k:
                ans += 1
            elif res[-1] > k:
                pos = res[-1] - k
                ans += math.comb(len(res), pos)

            res.append(res[-1] + 2**i)
            i+=1
        return ans
