class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 1:
            return 4
        if k == 0:
            return 2

        @cache
        def solve(i, jump, can_use):
            if i == k:
                return 1 + solve(i-1, jump, False)
            if i == k+1:
                return solve(i-1, jump, False)
            if i > k:
                return 0
            ans = 0
            ans += solve(i+2**jump, jump+1, True)
            if can_use:
                ans += solve(i-1, jump, False)
            return ans


        return solve(1, 0, True)
