class Solution:
    def waysToReachStair(self, k: int) -> int:
        jump_dis = 0
        ans = 0

        def cal(dn, jn):
            # print(dn, jn)
            if dn > jn + 1:
                return 0
            if dn == 0 == jn:
                return 1
            if dn == 0:
                return 1
            if jn == 0 and dn == 1:
                return 1
            if jn == 0 and dn > 1:
                return 0
            jn -= (dn - 1)
            if jn == 0:
                return 1
            return math.comb(jn + dn, dn)


        for jump_need in range(0, 34):
            if jump_need == 0:
                jump_dis = 0
            else:
                jump_dis |= (1 << (jump_need - 1))
            go_down_need = jump_dis + 1 - k
            if go_down_need < 0:
                continue
            if go_down_need > jump_need * 2 + 10:
                break
            ans += cal(go_down_need, jump_need)
            # print(ans)
        return ans
