class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(canDec,i,n):
            res=0
            if n==k:
                res+=1
            if canDec:
                res+=dfs(False,i,n-1)
            if n+2**i<=k+10:
                res+=dfs(True,i+1,n+2**i)
            return res
        return dfs(True,0,1)
