class Solution:
    def waysToReachStair(self, k: int) -> int:
        J, jump, res = 64, 1, 0

        if k == 0 or k == 1:
            res = 1

        for j in range(J):
            jump += 1 << j
            if jump < k:
                continue
            slack = jump - k
            if slack > j + 2:
                continue
            res += math.comb(j + 2, slack)

        return res
