class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def get_ans(pos, jump, last_is_back):
            ans = int(pos == k)
            if (new := pos + 2**jump) - k <= 2:
                ans += get_ans(new, jump + 1, False)
            if pos > 0 and not last_is_back:
                ans += get_ans(pos - 1, jump, True)
            return ans

        return get_ans(1, 0, False)
