#define LIM 30

bool preComputed = false;
int dp[LIM + 10][LIM + 10];

class Solution {
public:
    void preCompute() {
        if ( preComputed ) return;
        preComputed = true;
        for ( int i = 0; i <= LIM; i++ ) {
            for ( int j = 0; j <= i; j++ ) {
                if ( i == j || j == 0 ) dp[i][j] = 1;
                else dp[i][j] = dp[i - 1][j] + dp[i - 1][j - 1];
            }
        }
        return;
    }   

    int waysToReachStair(int k) {
        preCompute();
        int ans = 0;
        for ( int i = 0;; i++ ) {
            int jumpVal = (1 << i);
            if ( jumpVal < k ) continue;
            if ( jumpVal - i - 1 > k ) break;
            jumpVal = jumpVal - k;
            ans += dp[i + 1][jumpVal];
        }
        return ans;
    }
};
