class Solution {
public:
    int waysToReachStair(int k) {

        int pos = 1, jumps = 0, diff = pos-k;
        long long ans = 0;
        while(diff <= jumps+1){
            if(pos >= k){
                ans += ncr(jumps+1, diff);
            }
            pos = pos<<1;
            jumps++;
            diff = pos-k;
        }
        return ans;

    }

    long long ncr(int n, int r) {
        if(r > n - r) r = n - r;
        long long ans = 1;
        int i;

        for(i = 1; i <= r; i++) {
            ans *= n - r + i;
            ans /= i;
        }

        return ans;
    }
};
