class Solution {
public:
    int waysToReachStair(int k) {
        vector<vector<long long>> c(33, vector<long long>(33, 0LL));
        c[0][0] = 1LL;
        for (int i = 1; i < 33; ++i) {
            c[i][0] = c[i][i] = 1LL;
            for (int j = 1; j < i; ++j) {
                c[i][j] = c[i-1][j] + c[i-1][j-1];
            }
        }

        int cnt = 0;
        long long large = 0;
        long long ans = 0;
        while (large + (1LL<<cnt) - cnt - 2 + 1 <= k) {
            large += (1LL<<cnt);
            if (large + 1 >= k)     {
                int r = large + 1 - k;
                ans += c[cnt + 2][r];
            }
            cnt++;
        }
        if (k == 0 || k == 1)   ans++;
        return ans;
    }


};
