class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        power = 1
        for i in range(0, 30):
            tmp = 1+power-1 - k
            power *= 2

            if tmp > i+1 or tmp < 0:
                continue

            qq = 1
            tmp = min(tmp, i+1-tmp)

            for j in range(i+1-tmp+1, i+2):
                qq *= j
            for j in range(1, tmp+1):
                qq /= j

            ans += qq

        return int(ans)
